import React, { useEffect, useState } from 'react'
import { useEditor, EditorContent } from '@tiptap/react'
import { defaultExtensions } from '@tiptap/starter-kit'
import './styles.scss'

import MenuBar from './MenuBar';

import { useQuery, gql } from '@apollo/client';
import { LOAD_HUB } from '../../graphql/queries';

const Editor = () => {
  const [content, setContent] = useState(`
  <h2>
    Hi there,
  </h2>
  <p>
    this is a basic <em>basic</em> example of <strong>tiptap</strong>. Sure, there are all kind of basic text styles youâ€™d probably expect from a text editor. But wait until you see the lists:
  </p>
  <ul>
    <li>
      Thatâ€™s a bullet list with one â€¦
    </li>
    <li>
      â€¦ or two list items.
    </li>
  </ul>
  <p>
    Isnâ€™t that great? And all of that is editable. But wait, thereâ€™s more. Letâ€™s try a code block:
  </p>
  <pre><code class="language-css">body {
display: none;
}</code></pre>
  <p>
    I know, I know, this is impressive. Itâ€™s only the tip of the iceberg though. Give it a try and click a little bit around. Donâ€™t forget to check the other examples too.
  </p>
  <blockquote>
    Wow, thatâ€™s amazing. Good work, boy! ğŸ‘
    <br />
    â€” Mom
  </blockquote>
`);

  const { loading, error, data } = useQuery(LOAD_HUB);

  useEffect(() => {
    setContent(JSON.parse(data.getHub.description));
  }, [data, data.getHub.description]);

  const editor = useEditor({
    extensions: [
      ...defaultExtensions(),
    ],
    content: content,
    onUpdate({ editor }) {
      console.log(JSON.stringify(editor.getJSON()));
      console.log(editor.getHTML());
    },
  })


  return (
    <div>
      <MenuBar editor={editor} />
      <EditorContent editor={editor} />
    </div>
  )
}

export default Editor;
